THREAD_POOL = concurrent.futures.ThreadPoolExecutor(max_workers=100)

# –°–æ–∑–¥–∞–µ–º –ø—É–ª HTTP —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–π –¥–ª—è aiohttp
HTTP_SESSION = None

